import{_ as t,c as e,o as l,a3 as d}from"./chunks/framework.qnZckW2M.js";const c=JSON.parse('{"title":"取值与单位","description":"","frontmatter":{"title":"取值与单位","order":3,"group":{"title":"概念","order":1},"nav":{"title":"基础","order":1}},"headers":[],"relativePath":"KnowledgeBase/FrontEnd/CSS/concept/concept/取值与单位.md","filePath":"KnowledgeBase/FrontEnd/CSS/concept/concept/取值与单位.md","lastUpdated":1719733603000}'),a={name:"KnowledgeBase/FrontEnd/CSS/concept/concept/取值与单位.md"},i=d('<h1 id="取值与单位" tabindex="-1">取值与单位 <a class="header-anchor" href="#取值与单位" aria-label="Permalink to &quot;取值与单位&quot;">​</a></h1><h2 id="长度" tabindex="-1">长度 <a class="header-anchor" href="#长度" aria-label="Permalink to &quot;长度&quot;">​</a></h2><p>长度值适用于 <strong>水平</strong> 或 <strong>垂直</strong> 方向的尺寸。长度值表示为 <code>&lt;length&gt;</code>。长度值的格式是：</p><div class="language-css vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">number </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">+</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> 长度单位 = length</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>说明：</p><ul><li>注意一定要有单位，除非这个值是 <code>0</code>。如果长度值是 <code>0</code>，单位可有可无；</li><li>有些特性支持负的长度值。但是如果给一个不支持负长度值的特性设置一个负的值，那么这个声明会被忽略；</li><li>长度单位可分为 <strong>相对长度单位</strong> 和 <strong>绝对长度单位</strong>。</li></ul><h3 id="相对长度单位" tabindex="-1">相对长度单位 <a class="header-anchor" href="#相对长度单位" aria-label="Permalink to &quot;相对长度单位&quot;">​</a></h3><p>相对长度单位又可以分为 <strong>文本相对长度单位</strong> 和 <strong>视口相对长度单位</strong>。</p><h4 id="文本相对长度单位" tabindex="-1">文本相对长度单位 <a class="header-anchor" href="#文本相对长度单位" aria-label="Permalink to &quot;文本相对长度单位&quot;">​</a></h4><p>Font-relative Length Units</p><table tabindex="0"><thead><tr><th style="text-align:left;">长度单位</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">em</td><td style="text-align:left;">相对于当前对象内文本的字体尺寸。</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">ex</td><td style="text-align:left;">相对于字符 <code>x</code> 的高度。通常为字体高度的一半。</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">ch</td><td style="text-align:left;">相当于数字 <code>0</code> 的宽度。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">rem</td><td style="text-align:left;">相对于根元素（即 <code>&lt;html&gt;</code> 元素）<code>font-size</code> 计算值的倍数</td><td style="text-align:left;">CSS3</td></tr></tbody></table><h4 id="视口相对长度单位" tabindex="-1">视口相对长度单位 <a class="header-anchor" href="#视口相对长度单位" aria-label="Permalink to &quot;视口相对长度单位&quot;">​</a></h4><p>Viewport-relative Length Units</p><table tabindex="0"><thead><tr><th style="text-align:left;">长度单位</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">vw</td><td style="text-align:left;">相对于视口的宽度。视口被均分为 100 单位的 <code>vw</code>。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">vh</td><td style="text-align:left;">相对于视口的高度。视口被均分为 100 单位的 <code>vh</code>。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">vmax</td><td style="text-align:left;">相对于视口的宽度或高度，总是相对于大的那个。<br>视口的宽度或高度被均分为 100 单位的 <code>vmax</code>。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">vmin</td><td style="text-align:left;">相对于视口的宽度或高度，总是相对于小的那个。<br>视口的宽度或高度被均分为 100 单位的 vmin</td><td style="text-align:left;">CSS3</td></tr></tbody></table><h3 id="绝对长度单位" tabindex="-1">绝对长度单位 <a class="header-anchor" href="#绝对长度单位" aria-label="Permalink to &quot;绝对长度单位&quot;">​</a></h3><p>Absolute Length Units</p><table tabindex="0"><thead><tr><th style="text-align:left;">长度单位</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">px</td><td style="text-align:left;">像素（Pixels）</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">pt</td><td style="text-align:left;">点（Points）</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">pc</td><td style="text-align:left;">派卡（Picas）</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">cm</td><td style="text-align:left;">厘米</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">mm</td><td style="text-align:left;">毫米</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">q</td><td style="text-align:left;">1/4 毫米</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">in</td><td style="text-align:left;">英寸（Inches）</td><td style="text-align:left;">CSS1</td></tr></tbody></table><h2 id="角度" tabindex="-1">角度 <a class="header-anchor" href="#角度" aria-label="Permalink to &quot;角度&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">角度单位</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">deg</td><td style="text-align:left;">度（Degrees）</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">grad</td><td style="text-align:left;">梯度（Gradians）</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">rad</td><td style="text-align:left;">弧度（Radians）</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">turn</td><td style="text-align:left;">转、圈（Turns）</td><td style="text-align:left;">CSS3</td></tr></tbody></table><h2 id="时间" tabindex="-1">时间 <a class="header-anchor" href="#时间" aria-label="Permalink to &quot;时间&quot;">​</a></h2><p><code>&lt;time&gt;</code> 表达时间单位。</p><table tabindex="0"><thead><tr><th style="text-align:left;">时间单位</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">s</td><td style="text-align:left;">秒</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">ms</td><td style="text-align:left;">浩渺</td><td style="text-align:left;">CSS3</td></tr></tbody></table><ul><li>时间不允许有负值</li><li>时间单位包括有：<code>ms</code>、<code>s</code></li><li>单位换算：<code>1s = 1000ms</code></li></ul><h2 id="颜色" tabindex="-1">颜色 <a class="header-anchor" href="#颜色" aria-label="Permalink to &quot;颜色&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">名称</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;"><code>&lt;color&gt;</code></td><td style="text-align:left;">用于表示颜色的关键字或者数值规则</td><td style="text-align:left;">CSS1/CSS3</td></tr><tr><td style="text-align:left;">Color Name</td><td style="text-align:left;">用颜色名称来指定颜色。<br>包括基本颜色关键字、系统颜色、SVG 颜色关键字等。</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">HEX</td><td style="text-align:left;">十六进制记法。语法如 <code>#rrggbb</code> 或 <code>#rgb</code></td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">RGB</td><td style="text-align:left;">RGB 记法</td><td style="text-align:left;">CSS1</td></tr><tr><td style="text-align:left;">RGBA</td><td style="text-align:left;">RGBA 记法</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">HSL</td><td style="text-align:left;">HSL 记法</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">HSLA</td><td style="text-align:left;">HSLA 记法</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">transparent</td><td style="text-align:left;">全透明</td><td style="text-align:left;">CSS1/CSS3</td></tr><tr><td style="text-align:left;">currentColor</td><td style="text-align:left;">当前颜色</td><td style="text-align:left;">CSS3</td></tr></tbody></table><h2 id="文本" tabindex="-1">文本 <a class="header-anchor" href="#文本" aria-label="Permalink to &quot;文本&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">名称</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;">inherit</td><td style="text-align:left;">该值使得属性能够继承祖先设置。</td><td style="text-align:left;">CSS2.1</td></tr><tr><td style="text-align:left;">initial</td><td style="text-align:left;">属性初始值。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;">unset</td><td style="text-align:left;">擦除属性申明。</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>&lt;string&gt;</code></td><td style="text-align:left;">字符串（含转义字符串）。</td><td style="text-align:left;">CSS2</td></tr><tr><td style="text-align:left;"><code>&lt;url&gt;</code></td><td style="text-align:left;">在网页上提供一个资源地址。（图像、声频、视频或浏览器支持的其他任何资源）</td><td style="text-align:left;">CSS2</td></tr><tr><td style="text-align:left;"><code>&lt;identifier&gt;</code></td><td style="text-align:left;">使用用户自定义标识名作为组件取值。</td><td style="text-align:left;">CSS2</td></tr></tbody></table><h2 id="图像" tabindex="-1">图像 <a class="header-anchor" href="#图像" aria-label="Permalink to &quot;图像&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">名称</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;"><code>&lt;image&gt;</code></td><td style="text-align:left;">用来表示一个 2D 图像</td><td style="text-align:left;">CSS1/CSS3</td></tr><tr><td style="text-align:left;"><code>image()</code></td><td style="text-align:left;">用于指定图像活着图像替代</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>image-set()</code></td><td style="text-align:left;">根据用户设备的分辨率匹配合适的图像</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>&lt;gradient&gt;</code></td><td style="text-align:left;">使用简单的语法实现颜色渐变，以便 UA 在渲染页面自动生成图像</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>linear-gradient()</code></td><td style="text-align:left;">用线性渐变创建图像</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>radial-gradient()</code></td><td style="text-align:left;">用径向渐变创建图像</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>repeating-linear-gradient()</code></td><td style="text-align:left;">用重复的线形渐变创建图像</td><td style="text-align:left;">CSS3</td></tr><tr><td style="text-align:left;"><code>repeating-radial-gradient()</code></td><td style="text-align:left;">用重复的径向渐变创建图像</td><td style="text-align:left;">CSS3</td></tr></tbody></table><h2 id="数字" tabindex="-1">数字 <a class="header-anchor" href="#数字" aria-label="Permalink to &quot;数字&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">名称</th><th style="text-align:left;">说明</th><th style="text-align:left;">版本</th></tr></thead><tbody><tr><td style="text-align:left;"><code>&lt;number&gt;</code></td><td style="text-align:left;">浮点数</td><td style="text-align:left;">CSS2</td></tr><tr><td style="text-align:left;"><code>&lt;integer&gt;</code></td><td style="text-align:left;">整数</td><td style="text-align:left;">CSS2</td></tr><tr><td style="text-align:left;"><code>&lt;percentage&gt;</code></td><td style="text-align:left;"><code>&lt;number&gt;</code> 接 <code>%</code></td><td style="text-align:left;">CSS2</td></tr></tbody></table><ul><li>百分比总是相对于另一个值才存在的。</li></ul><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ul><li><a href="https://juejin.im/post/594589fc8d6d81cc72e1ca66" target="_blank" rel="noreferrer">📝 CSS 中的尺寸单位</a></li></ul>',34),n=[i];function s(r,g,o,x,f,y){return l(),e("div",null,n)}const S=t(a,[["render",s]]);export{c as __pageData,S as default};
